public class Program5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		stars1 (); //displays first method
		stars2 (); //displays second method
		stars3 (); //displays thrid method
	}
	public static void stars1 () {
		System.out.println("*****"); //displays stars
		System.out.println("*****");
		System.out.println(" * * ");
		System.out.println("  *  ");
		System.out.println(" * * ");
	}
	public static void stars2 () {
		System.out.println("\n\n*****"); //starts two new lines and displays stars
		System.out.println("*****");
		System.out.println(" * * ");
		System.out.println("  *  ");
		System.out.println(" * * ");
		System.out.println("*****");
		System.out.println("*****");
	}
	public static void stars3 () {
		System.out.println("\n\n  *  "); //starts two new lines and displays stars
		System.out.println("  *  ");
		System.out.println("  *  ");
		System.out.println("*****");
		System.out.println("*****");
		System.out.println(" * * ");
		System.out.println("  *  ");
		System.out.println(" * * ");
	}
}
